local a={}local b=require("filesystem")function a.readFile(c)local d={}if not b.exists(c)then return{}end;local e=io.open(c,"r")for f in e:lines()do table.insert(d,f)end;e:close()return d end;function a.readAll(c)local d={}local g=a.readFile(c)for h,i in pairs(g)do local j,k=string.match(i,"(.*)%s=%s(.*)")if not j then j,k=string.match(i,"(.*)=(.*)")end;if j then d[j]=k end end;return d end;function a.write(c,j,k)local l=a.readAll(c)l[j]=k;if b.exists(c)then b.remove(c)end;local m=io.open(c,"w")for n,o in pairs(l)do m:write(n," = ",o,"\n")end;m:close()end;function a.append(c,...)b.makeDirectory(b.path(c))local m=io.open(c,"a")local p={...}for q=1,#p do m:write(p[q],"\n")end;m:close()end;return a
